import PouchDB from '@rasgo/pouchdb-core';

import IDBPouch from '@rasgo/pouchdb-adapter-idb';
import HttpPouch from '@rasgo/pouchdb-adapter-http';
import mapreduce from '@rasgo/pouchdb-mapreduce';
import replication from '@rasgo/pouchdb-replication';

PouchDB.plugin(IDBPouch)
  .plugin(HttpPouch)
  .plugin(mapreduce)
  .plugin(replication);

export default PouchDB;
